import PropTypes from 'prop-types'
import React, { Component } from 'react'
import FlipCard from 'react-native-flip-card'
import LinearGradient from 'react-native-linear-gradient'

import {
  View,
  Text,
  Image,
  Platform,
  StyleSheet,
} from 'react-native'

import Icons from './Icons'

const ChipIcon = 'data:image/png;base64,'
const Watermark = 'data:image/png;base64,'

const BASE_SIZE = { width: 300, height: 190 }

const s = StyleSheet.create({
  cardContainer: {},
  cardFace: {},
  chip: {
    position: 'absolute',
    top: 20,
    right: 15,
    width: 60,
    height: 40,
    resizeMode: 'contain',
  },
  watermark: {
    position: 'absolute',
    top: 20,
    right: 20,
    width: 140,
    height: 140,
    opacity: 0.05
  },
  icon: {
    position: 'absolute',
    top: 20,
    left: 15,
    width: 60,
    height: 40,
    resizeMode: 'contain',
  },
  iconBack: {
    position: 'absolute',
    bottom: 15,
    right: 25,
    width: 60,
    height: 40,
    resizeMode: 'contain',
  },
  blackBar: {
    position: 'absolute',
    top: 20,
    left: 0,
    right: 0,
    height: 50,
    width: '100%',
    backgroundColor: '#232a33',
  },
  grayBar: {
    top: 80,
    left: 20,
    right: 0,
    height: 45,
    width: '60%',
    position: 'absolute',
    backgroundColor: '#656464',
  },
  baseText: {
    color: 'rgba(255, 255, 255, 0.8)',
    backgroundColor: 'transparent',
  },
  placeholder: {
    color: 'rgba(255, 255, 255, 0.5)',
  },
  focused: {
    fontWeight: 'bold',
    color: 'rgba(255, 255, 255, 1)',
  },
  number: {
    fontSize: 21,
    position: 'absolute',
    top: 95,
    left: 28,
  },
  name: {
    fontSize: 12,
    position: 'absolute',
    bottom: 20,
    left: 25,
    right: 100,
  },
  expiryLabel: {
    fontSize: 9,
    position: 'absolute',
    bottom: 40,
    left: 218,
  },
  expiry: {
    fontSize: 12,
    position: 'absolute',
    bottom: 20,
    left: 220,
  },
  amexCVC: {
    fontSize: 16,
    position: 'absolute',
    top: 73,
    right: 30,
  },
  cvc: {
    fontSize: 16,
    position: 'absolute',
    top: 80,
    right: 30,
  },
})

/* eslint react/prop-types: 0 */ // https://github.com/yannickcr/eslint-plugin-react/issues/106
export default class CardView extends Component {
  static propTypes = {
    focused: PropTypes.string,

    brand: PropTypes.string,
    name: PropTypes.string,
    number: PropTypes.string,
    expiry: PropTypes.string,
    cvc: PropTypes.string,
    placeholder: PropTypes.object,

    scale: PropTypes.number,
    fontFamily: PropTypes.string,
    imageFront: PropTypes.number,
    imageBack: PropTypes.number,
    cardGradientColors: PropTypes.array,
  }

  static defaultProps = {
    name: '',
    placeholder: {
      number: 'XXXX XXXX XXXX XXXX',
      name: 'NOME NO CART√ÉO',
      expiry: 'VALIDADE',
      cvc: 'XXX',
    },

    scale: 1,
    fontFamily: Platform.select({ ios: 'Courier', android: 'monospace' }),
    imageFront: require('../images/card-front.png'),
    imageBack: require('../images/card-back.png'),
    cardGradientColors: ['#bc8743', '#c37f16'],
  }

  render() {
    const {
      focused, cardGradientColors,
      brand, name, number, expiry, cvc, watermark,
      placeholder, imageFront, imageBack, scale, fontFamily,
    } = this.props

    const isAmex = brand === 'american-express'
    const shouldFlip = !isAmex && focused === 'cvc'

    const containerSize = { ...BASE_SIZE, height: BASE_SIZE.height * scale }
    const transform = {
      transform: [
        { scale },
        { translateY: ((BASE_SIZE.height * (scale - 1) / 2)) },
      ],
    }

    return (
      <View style={[s.cardContainer, containerSize]}>
        <FlipCard
          friction={10}
          flipHorizontal
          clickable={false}
          flip={shouldFlip}
          perspective={2000}
          flipVertical={false}
          style={{ borderWidth: 0 }}
        >
          <View style={[transform, { borderRadius: 10, overflow: 'hidden' }]}>
            <LinearGradient
              colors={cardGradientColors}
              style={[BASE_SIZE, s.cardFace]}
            >
              <Image style={[s.watermark]} source={{ uri: Watermark }}/>
              <Image style={[s.icon]} source={{ uri: Icons[brand] }}/>
              <Image style={[s.chip]} source={{ uri: ChipIcon }}/>
              {/*{watermark ? (*/}
              {/*) : null}*/}
              <View
                style={[
                  {
                    top: 70,
                    left: 40,
                    padding: 5,
                    width: '73%',
                    borderWidth: 1,
                    borderRadius: 10,
                    position: 'absolute',
                    alignItems: 'center',
                    borderColor: 'rgba(255, 255, 255, 0.3)',
                  },
                  transform,
                ]}
              >
                <Text
                  style={[
                    s.baseText,
                    { fontFamily, fontSize: 16, width: '95%' },
                    !number && s.placeholder,
                    focused === 'number' && s.focused,
                  ]}
                >
                  {!number ? placeholder.number : number}
                </Text>
              </View>
              <Text
                numberOfLines={1}
                style={[s.baseText, { fontFamily }, s.name, !name && s.placeholder, focused === 'name' && s.focused]}
              >
                {!name ? placeholder.name : name.toUpperCase()}
              </Text>
              {/* Empty autoclose tag, justo have something */}
              <Text
                style={[s.baseText, { fontFamily }, s.expiryLabel, s.placeholder, focused === 'expiry' && s.focused]}/>
              <Text
                style={[s.baseText, { fontFamily }, s.expiry, !expiry && s.placeholder, focused === 'expiry' && s.focused]}>
                {!expiry ? placeholder.expiry : expiry}
              </Text>
              {isAmex && (
                <Text
                  style={[s.baseText, { fontFamily }, s.amexCVC, !cvc && s.placeholder, focused === 'cvc' && s.focused]}>
                  {!cvc ? placeholder.cvc : cvc}
                </Text>
              )}
            </LinearGradient>
          </View>
          <View style={[transform, { borderRadius: 10, overflow: 'hidden' }]}>
            <LinearGradient
              colors={cardGradientColors}
              style={[BASE_SIZE, s.cardFace]}
            >
              <Image style={[s.watermark]} source={{ uri: Watermark }}/>
              <View style={s.blackBar} />
              <View style={s.grayBar} />
              <View style={[s.cvc, { backgroundColor: 'white', padding: 13, borderRadius: 10 }]}>
                <Text style={[s.baseText, !cvc && s.placeholder, focused === 'cvc' && s.focused, { color: '#666360' }]}>
                  {!cvc ? placeholder.cvc : cvc}
                </Text>
              </View>
              <Image style={[s.iconBack]} source={{ uri: Icons[brand] }}/>
            </LinearGradient>
          </View>
        </FlipCard>
      </View>
    )
  }
}
